      // create a style to display our position history (track)
      const MongoClient = require('mongodb').MongoClient;
      // Create a serializer and define a header.
      
      const url = 'mongodb://localhost:27017';
      const dbName = 'tapDB';
      const client = new MongoClient(url);
      var obj = {
        table: []
     };


      var Markers= [];  
          var trackStyle = new ol.style.Style({
        stroke: new ol.style.Stroke({
          color: 'transparent',
          width: 3,
          lineCap: 'round'
        })
      });
      // use a single feature with a linestring geometry to display our track
      var trackFeature = new ol.Feature({
        geometry: new ol.geom.LineString([])
      });
      var features =[]
      // we'll need a vector layer to render it
      var trackLayer = new ol.layer.Vector({
        source: new ol.source.Vector({

          features: [trackFeature]

        }),
        style: trackStyle
      });

      var baseLayer = new ol.layer.Tile({
        source: new ol.source.OSM()
      });

      var london = ol.proj.transform([-0.12755, 51.507222], 'EPSG:4326', 'EPSG:3857');
      var view = new ol.View({
        center: london,
        zoom: 15
      });

      var map = new ol.Map({
        target: 'map',
        layers: [
          baseLayer,
          trackLayer,
          new ol.layer.Tile({source: new ol.source.OSM()})
        ],
        view: view

      });

    function markerUpdater(){

      console.log('marked')
    for(var i = 0; i < Markers.length; i++) {
    var item = Markers[i];
    var longitude = item[0];
    var latitude = item[1];
    console.log(longitude)
    console.log(latitude)

    var iconFeature = new ol.Feature({
        geometry: new ol.geom.Point(ol.proj.transform([longitude, latitude], 'EPSG:4326', 'EPSG:3857'))
    });

    var iconStyle = new ol.style.Style({
        image: new ol.style.Icon(({
            anchor: [0.5, 1],
            src: "http://cdn.mapmarker.io/api/v1/pin?text=&size=50&hoffset=1"
        }))
    });

    iconFeature.setStyle(iconStyle);
    features.push(iconFeature);

    }
    var vectorSource = new ol.source.Vector({
        features: features
    });

    var vectorLayer = new ol.layer.Vector({
        source: vectorSource
    });
    map.addLayer(vectorLayer);
  }
      // set up the geolocation api to track our position
      var geolocation = new ol.Geolocation({
        tracking: true
      });
      // bind the view's projection
      geolocation.bindTo('projection', view);
      // when we get a position update, add the coordinate to the track's
      // geometry and recenter the view


      geolocation.on('change:position', function() {
        var coordinate = geolocation.getPosition();
        view.setCenter(coordinate);
        console.log(coordinate)
        trackFeature.getGeometry().appendCoordinate(coordinate);
      });

      // put a marker at our current position
      var marker = new ol.Overlay({
        element: document.getElementById('location'),
        positioning: 'center-center'
      });
      map.addOverlay(marker);
      marker.bindTo('position', geolocation);
      // rotate the view to match the device orientation
      var deviceOrientation = new ol.DeviceOrientation({
        tracking: true
      });
      deviceOrientation.on('change:heading', onChangeHeading);

      window.addEventListener('load', function(){
        markerUpdater()

        console.log('lol2')

        var coordinate = geolocation.getPosition();
        console.log(coordinate)
        view.setCenter(coordinate);
      })
      document.getElementById('reCenterIt').addEventListener('click', function(){
        var coordinate = geolocation.getPosition();
        console.log(coordinate)
        view.setCenter(coordinate);
        view.setZoom(16.5)
      })

      
      function onChangeHeading(event) {
        var heading = event.target.getHeading();
        view.setRotation(-heading);
      }

      document.getElementById('createMarker').addEventListener('click', function(){
        var coordinate = geolocation.getPosition();
        var realCoordinates = ol.proj.transform(coordinate, 'EPSG:3857', 'EPSG:4326')
        obj.table.push({id: 1, square:2});
